export function getTimeDiffInSeconds({ date }) {
    const nowInSeconds = Math.floor(new Date().getTime() / 1000); // current time in seconds
    const dateInSeconds = Math.floor(new Date(date).getTime() / 1000); // provided date in seconds

    // Calculate the time difference in seconds
    const diff = dateInSeconds - nowInSeconds;

    return diff > 0 ? diff : 0; // Return the difference or 0 if the provided date is in the future
}
