import React, { useState, useEffect, } from 'react';
import axios from 'axios';
import QRCode from "react-qr-code";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useParams } from 'react-router-dom';
import jwt_decode from 'jwt-decode';  // import the jwt-decode library
import './App.css'; // Importar o arquivo CSS

export default function App() {
  const { id, jwt } = useParams();

  const [ price, setPrice ] = useState(null);
  const [ qrCode, setQrCode ] = useState(null);
  const [ logo, setLogo ] = useState(null);
  const [ backgroundColor, setBackgroundColor ] = useState(null);
  const [ token, setToken ] = useState(null);
  const [ userData, setUserData ] = useState(null);

  useEffect(() => {
    // Decode jwt
    const decodedToken = jwt_decode(jwt);

    if (decodedToken) {
      setToken(decodedToken.token);

      // Fetch first API with JWT in headers
      axios.get(`https://node.clubecerto.com.br/superapp/pay/payment/status/${ id }/`, {
        headers: {
          Authorization: `Bearer ${ decodedToken.token }`
        }
      })
        .then((response) => {
          const qrcodeData = response.data;
          setPrice(qrcodeData.value);
          setQrCode(qrcodeData.qrCode);

          // Access the rest of the data from the decoded JWT
          setLogo(decodedToken.selectedCompany.companiesImage.image);
          setBackgroundColor(decodedToken.selectedCompany.companiesColor.backgroundColor);
          setUserData(decodedToken);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [ id, jwt ]);

  const copyCode = async (token) => {
    try {
      await
        navigator.clipboard.writeText(token);

      toast.success("Código Copiado!", {
        position: "bottom-center",
        autoClose: 2000,
      });
    } catch (err) {
      console.error("Failed to copy text: ", err);
    }
  };

  return (
    <div className="main">
      <div className="header" style={ { backgroundColor: backgroundColor } }>
        <div className='image-container'>
          <img src={ logo } alt="Logo" className="logo" />
        </div>
        <p className="instruction">
          Falta pouco! Escaneie o código QR pelo seu app de pagamentos ou Internet Banking
        </p>
      </div>

      <div className="qr-section">
        { qrCode && (
          <QRCode
            className='qr-code'
            value={ qrCode }
            viewBox={ `0 0 256 256` }
          />
        ) }
        <p className="qr-value">R$ { price }</p>
        <p className="small-text">Se preferir, você pode pagá-lo copiando e colando o código abaixo:</p>
        <p className="copy-paste-code">{ token }</p>
        <button className="copy-btn" onClick={ () => copyCode(token) }>Copiar código</button>
        <ToastContainer></ToastContainer>
      </div>

      { !qrCode && <h1 style={ { textAlign: 'center', marginTop: '2rem' } }>Not Found</h1> }
    </div>
  );
}
